import React from 'react'

const baseURL = "/api/movies/"

const Item = ({id, name, plot, poster}) => {
  const link = baseURL + id

  return (
    <div className="row-item">
      <a href={link}>
        <img src={poster} alt={name}/>
      </a>
    </div>
  )
}

export default Item
