import React, {useState, useEffect} from 'react'
import Item from './Item'
const ContentRow = ({movies}) => {

  const moviesRows = () => movies.map(movie =>
    <
    Item
    id={movie.id}
    name={movie.name}
    plot={movie.plot}
    poster={movie.poster}
    link={movie.fileLocation}
    />
  )

  return (
    <div className="content-row">
      {moviesRows()}
    </div>
  )
}

export default ContentRow
