import React from 'react'
import moviesService from '../service/movie'

const Item = ({id, name, plot, poster}) => {
  let link = ""

  const onClick = () => {
    changeToApi(id)
  }

  const changeToApi = id => {
    moviesService
      .getMovie(id)
      .then(response =>
        link = response.data
      )
  }

  return (
    <div className="row-item">
      <a href={link}>
        <img src={poster} alt={name}/>
      </a>
    </div>
  )
}

export default Item
